IN pFilename VARCHAR(255),
IN pPhoto LONGTEXT,

/*Product Details data*/
IN pQuantity FLOAT)
BEGIN
	START TRANSACTION;
		/*We update product header*/
		UPDATE products 
		SET sku = pSku,
				`name` = pName,
				description = pDescription,
				price = pPrice,
				size = pSize,
				min_value = pMinValue,
				max_value = pMaxValue,
				file_name = pFilename,
				photo = pPhoto
		WHERE id = pId;
		
		/*We update product detail*/
		UPDATE product_details
		SET quantity = pQuantity
		WHERE product_id = pId;
	COMMIT;
END
;;
delimiter ;

