-- ----------------------------
-- Procedure structure for updatePurchaseOrder
-- ----------------------------
DROP PROCEDURE IF EXISTS `updatePurchaseOrder`;
delimiter ;;
CREATE PROCEDURE `updatePurchaseOrder`(IN pId INT, IN pStatus INT)
BEGIN
	DECLARE quantity_, feedstock_id_ INT;
	DECLARE listo BOOLEAN DEFAULT FALSE;
	DECLARE cursor_detail CURSOR FOR SELECT buy_order_details.feedstock_id, buy_order_details.quantity
																	 FROM buy_order_details
																	 INNER JOIN buy_orders
																		 ON buy_orders.id = buy_order_details.buy_order_id
																	 WHERE buy_orders.id = pId;
	DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET listo = TRUE;
	
	START TRANSACTION;
		UPDATE buy_orders
		SET buy_order_status_id = pStatus
		WHERE id = pid;
		
		IF(pStatus = 3) THEN
			UPDATE buy_orders
			SET buy_order_status_id = pStatus
			WHERE id = pid;
			
			OPEN cursor_detail;
			cursor_detail_loop: LOOP
				FETCH cursor_detail INTO feedstock_id_, quantity_;
				
				IF listo THEN
					LEAVE cursor_detail_loop;
				END IF;
				
				UPDATE feedstock_details
				SET quantity = quantity + quantity_
				WHERE id = feedstock_id_;
			END LOOP;
			CLOSE cursor_detail;
		END IF;
	COMMIT;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for updateRecipe
-- ----------------------------
DROP PROCEDURE IF EXISTS `updateRecipe`;
delimiter ;;
CREATE PROCEDURE `updateRecipe`(IN pId INT,
IN pProduct_id INT,
IN pDescription VARCHAR(255),

/*Recipe detail*/
IN pJson_details JSON)
BEGIN
	DECLARE i INT;

	START TRANSACTION;
		/*We update the recipe header*/
		UPDATE recipes
		SET product_id = pProduct_id,
				description = pDescription
		WHERE id = pId;
		
		DELETE FROM recipe_details
		WHERE recipe_id = pId;
		
		/*Let's go through the detail arrangement of the recipe*/
		SET i = 0;
		WHILE i < JSON_LENGTH(pJson_details) DO
			SET @recipe_detail_json = JSON_EXTRACT(pJson_Details, CONCAT( '$[', i, ']' ));
			SET @_quantity = JSON_EXTRACT(@recipe_detail_json, '$.quantity');
			SET @_feedstock_id = JSON_EXTRACT(@recipe_detail_json, '$.feedstock_id');
			
			/*We insert the new recipe detail*/
			INSERT INTO recipe_details(recipe_id, feedstock_id, quantity)
			VALUES(pId, @_feedstock_id, @_quantity);
			SET i = i + 1;
		END WHILE;
	COMMIT;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for updateSaleOrder
-- ----------------------------
DROP PROCEDURE IF EXISTS `updateSaleOrder`;
delimiter ;;
CREATE PROCEDURE `updateSaleOrder`(IN pId INT, IN pStatus INT)
BEGIN
	DECLARE quantity_, detail_id_ INT;
	DECLARE listo BOOLEAN DEFAULT FALSE;
	DECLARE cursor_detail CURSOR FOR SELECT product_details.id, product_details.quantity
																	 FROM product_details
																	 INNER JOIN sale_orders_details
																		 ON sale_orders_details.product_id = product_details.product_id
																	 INNER JOIN sale_orders
																		 ON sale_orders.id = sale_orders_details.sale_orders_id
																	 WHERE sale_orders.id = pId;
	DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET listo = TRUE;
	
	START TRANSACTION;
		/*We update the status of the sales order to the new one we obtain in the function parameters*/
		UPDATE sale_orders
		SET sale_orders_status_id = pStatus
		WHERE id = pId;
		
		IF(pStatus = 4) THEN
			UPDATE sale_orders
			SET sale_orders_status_id = pStatus
			WHERE id = pId;
		
			OPEN cursor_detail;
			cursor_detail_loop: LOOP
				FETCH cursor_detail INTO detail_id_, quantity_;
				IF listo THEN
					LEAVE cursor_detail_loop;
				END IF;
			
				UPDATE product_details
				SET quantity = quantity - quantity_
				WHERE id = detail_id_;
			
			END LOOP;
			CLOSE cursor_detail;
		END IF;
	COMMIT;
END
;;
delimiter ;

